window.addEventListener('load', function () {
  const ajaxPost = document.querySelector('.ajax-post');
  if (ajaxPost) {
    ajaxPost.addEventListener('click', sendAjaxPost);
  }
});
function sendAjaxPost(event) {
  event.preventDefault();
  const removeCourse = confirm('Tem certeza que deseja excluir este curso?');
  if (!removeCourse) return;
  const elem = event.target;
  const url = elem.dataset.url;
  const method = elem.dataset._method;
  const formData = new FormData();
  formData.append('_method', method);
  formData.append('url', url);
  const headers = new Headers();
  headers.append('Content_Type', 'x-www-form-urlencoded');
  fetch(url, {
    method: 'POST',
    body: formData
  }).then(response => response.json()).then(data => {
    if (data.redirect) {
      location.href = data.redirect;
    }
  });
}
window.addEventListener('load', function () {
  let form = document.querySelector('.form-post');
  if (form) form.addEventListener('submit', sendPostForm);
});
async function sendPostForm(event) {
  event.preventDefault();
  let data = new FormData(this);
  let files = document.querySelectorAll('[type="file"]');
  if (files.length) {
    files.forEach((file, i) => {
      data.delete([file.name]);
      data.append(`${file.name}`, file.files[0]);
    });
  }
  let url = this.action;
  fetch(url, {
    method: 'POST',
    body: data
  }).then(response => response.json()).then(data => {
    if (data.message) {
      document.querySelector('.ajax-response').innerHTML = data.message;
    }
    if (data.redirect) {
      location.href = data.redirect;
    }
  });
}
let menuToggle = event => {
  let menuDropDown = document.querySelector('.menu .drop-down');
  if (menuDropDown) {
    menuDropDown.addEventListener('click', () => {
      let subMenu = document.querySelector('.menu .sub-menu');
      if (subMenu) subMenu.classList.toggle('active');
    });
  }
};
window.addEventListener('load', menuToggle);
var slideIndex = 0;
window.addEventListener('load', function () {
  showSlides(slideIndex);
});
function plusSlides(n) {
  showSlides(slideIndex += n);
}
function currentSlide(n) {
  showSlides(slideIndex = n);
}
function showSlides(n) {
  let i;
  let slides = document.getElementsByClassName("mySlides");
  if (slides.length) {
    let dots = document.getElementsByClassName("dot");
    if (n > slides.length) {
      slideIndex = 1;
    }
    if (n < 1) {
      slideIndex = slides.length;
    }
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
  }
}